import {put, call, all, takeLatest } from "redux-saga/effects"
import {cartActionType, cartActionTypes} from "./cart.types"

//saga to clear all cart items

function *clearAllCartItemsAsync(){
 yield console.log("fbdbfd")
}

function *clearAllCartItems(){
    yield takeLatest(cartActionTypes.CLEAR_ALL_CART_ITEMS, clearAllCartItemsAsync)
}



export function *cartSagas(){
    yield all([
        call(clearAllCartItems),
        
               
          
    ])
}
