import {createSelector} from "reselect"



const selectCart = state => state.cart   //input selector, access the cart obj from state

export const selectCartHidden = createSelector(
    [selectCart],
    cart=> cart.hidden
)


export const selectCartItems = createSelector(
    [selectCart] ,
    cart => cart.cartItems
)

export const selectCartItemsCount = createSelector(
    [selectCartItems], 
    cartItems => cartItems.map((accumulatedQuantity, currentItemQuantity) =>
    accumulatedQuantity + currentItemQuantity.quantity, 0) 
)

export const selectCartItemsTotalPrice = createSelector(
    [selectCartItems], 
    cartItems => cartItems.map((accumulatedTotalPrice, currentItem) =>
    accumulatedTotalPrice + (currentItem.price * currentItem.quantity), 0) 
)
