import {SpinnerOverlay, SpinnerContainer } from "./with-spinner.styles"

const WithSpinner = WrappedComponent => { 
    const spinner = ({isLoading,  ...otherProps}) => {

    return isLoading ? (
    <SpinnerOverlay >
    <SpinnerContainer>
    
    </SpinnerContainer>

    </SpinnerOverlay>
    ) : (
    <WrappedComponent {...otherProps} />

    )}
return spinner
}

    export default WithSpinner
