import {takeLatest, put, call, all} from "redux-saga/effects"

import { userActionTypes} from "./user.types"

import {auth, googleprovider, createUserProfileDocument, SignInWithGoogle} from "../../firebase/firebase.utils"

//import {googleSignInStart, emailSignInStart} from "./users.actions"

//import {createUserProfileDocument} from "../../firebase/firebase.utils"

export function* googleSignInAsyc(){
    //yield console.log("hello")

    try {
        const userRef = yield auth.signInWithPopup(googleprovider) //not using the fx signinwithgoogle here, bcz we want access to the value returned from this method
        console.log(userRef)
    }catch(error){

    }

}

export function *googleSignInStart(){
    yield takeLatest(userActionTypes.GOOGLE_SIGNIN_START, googleSignInAsyc)
}

export function *userSagas(){
    yield all([call(googleSignInStart)])
}
